import React from 'react';
import './ButtonNav.less';

class ButtonNav extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        var tab = this.props.tab;
        tab = tab || 'info';

        if (!this.props.nav) {
            return (
                <div className="container-fluid button-nav" ref="el">
                    <div className="row">
                        <button className="col-md-6 btn btn-default" disabled>商圈信息</button>
                        <button className="col-md-6 btn btn-default" disabled>商户信息</button>
                    </div>
                </div>
            );
        }

        return (
            <div className="container-fluid button-nav" ref="el">
                <div className="row">
                    <button className={tab === 'info' ? 'col-md-6 btn btn-info' : 'col-md-6 btn btn-default'} data-node="link" data-to="info">商圈信息</button>
                    <button className={tab === 'list' ? 'col-md-6 btn btn-info' : 'col-md-6 btn btn-default'} data-node="link" data-to="list">商户信息</button>
                </div>
            </div>
        );
    }


}

export default ButtonNav;