import React from 'react';
import AoiNav from 'components/common/aoinav/AoiNav';
import './Info.less';

class AreaInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    render() {
        return (
            <div className="area-info" onClick={this.props.onClick}>
                <div className="info-panel panel panel-default">
                    {
                        (function (state) {
                            if (state.isAoi) {
                                return (
                                    <section className="info-item panel-body">
                                        <h4 className="info-item-header">商户信息</h4>
                                        <p>
                                            该商圈内商户平均配送范围<span className="count_num">{state.avg_area}</span>平方公里
                                        </p>
                                    </section>
                                );
                            }
                            return (
                                <section className="info-item panel-body">
                                    <h4 className="info-item-header">城市信息</h4>
                                    <p>
                                        商户平均配送范围<span className="count_num">{state.avg_area}</span>平方公里
                                    </p>
                                </section>
                            );
                        })(this.state)
                    }
                    {
                        (function (state) {
                            if (state.isAoi) {
                                return (
                                    <section className="info-item panel-body">
                                        <h4 className="info-item-header">优化建议</h4>
                                        <ul>
                                            <li>
                                                配送范围过大的商户
                                                <a className="count_num" href="javascript:void(0);"
                                                    data-node="link" data-to="list"
                                                    data-url="/wlmine/shopdeliverlist"
                                                    data-mode="large"
                                                >
                                                    {state.deliver_region_large_shops_sum}
                                                </a>家，建议缩小商户的配送范围
                                            </li>
                                            <li>
                                                发生重叠的商户品牌一共
                                                <a className="count_num" href="javascript:void(0);"
                                                    data-node="link" data-to="list"
                                                    data-url="/wlmine/shopoverlapbrandv2"
                                                    data-mode="overlap"
                                                >
                                                    {state.overlap_sum}
                                                </a>
                                                个，建议将重叠的配送范围划给品牌中的一家商户。
                                            </li>
                                        </ul>
                                    </section>
                                );
                            }
                            return (
                                <section className="info-item panel-body">
                                    <h4 className="info-item-header">优化建议</h4>
                                    <ul>
                                        <li>
                                            配送范围过大的商户
                                            <a className="count_num"  href="javascript:void(0);" data-node="link" data-to="list">
                                                {state.deliver_region_large_shops_sum}
                                            </a>
                                            家，建议缩小商户的配送范围
                                        </li>
                                        <li>
                                            发生重叠的商户品牌一共
                                            <span className="count_num">
                                                {state.overlap_sum}
                                            </span>
                                            个，建议将重叠的配送范围划给品牌中的一家商户。
                                        </li>
                                    </ul>
                                </section>
                            );
                        })(this.state)
                    }
                </div>
            </div>
        );
    }

    setData(data) {
        this.setState(data);
    }
}

export default AreaInfo;


