import React from 'react';
import './info.less';

class CostInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    render() {
        return (
            <div className="cost-info" onClick={this.props.onClick}>
                <div className="info-panel panel panel-default">
                    {
                        function (state) {
                            if (state.isAoi) {
                                return null;
                            }
                            return (
                                <section className="info-item panel-body">
                                    <h4 className="info-item-header">城市信息</h4>
                                    <p>
                                        一共
                                        &nbsp;
                                        <span className="count_num">
                                            {state.aoi_sum}
                                        </span>
                                        商圈，
                                        &nbsp;
                                        <span className="count_num">
                                            {state.shop_sum}
                                        </span>
                                        &nbsp;
                                        家商户。
                                    </p>
                                </section>
                            );
                        }(this.state)
                    }
                   
                    <section className="info-item panel-body">
                        <h4 className="info-item-header">商户信息</h4>
                        <p>
                            一共
                            <a className="count_num" href="javascript:;" data-node="link" data-to="list" data-param="h_profit">{this.state.h_profit}</a>
                                &nbsp;家高收益商户，
                            <a className="count_num" href="javascript:;" data-node="link" data-to="list" data-param="m_profit">{this.state.m_profit}</a>
                                &nbsp;家中收益商户，
                            <a className="count_num" href="javascript:;" data-node="link" data-to="list" data-param="l_profit">{this.state.l_profit}</a>
                                &nbsp;家低收益商户
                        </p>
                    </section>
                    <section className="info-item panel-body">
                        <h4 className="info-item-header">优化建议</h4>
                        <ol>
                            <li className="advise-item">
                                平均配送距离过长商户
                                &nbsp;
                                <a className="count_num" href="javascript:;" data-node="link" data-to="list" data-param="distance_long">
                                    {this.state.distance_long}
                                </a>
                                &nbsp;家商户。
                                可考虑缩小这些商户的配送范围。
                            </li>
                            <li className="advise-item">
                                每单平均耗时过长的商户
                                &nbsp;
                                <a className="count_num" href="javascript:;" data-node="link" data-to="list" data-param="costtime_long">
                                    {this.state.costtime_long}
                                </a>
                                &nbsp;家商户。
                                可考虑加快餐厅出餐时间，增加绿色通道。
                            </li>
                        </ol>
                    </section>
                </div>
            </div>
        );
    }

    setData(data) {
        this.setState(data);
    }
}

export default CostInfo;