import React from 'react';
import './List.less';

var template = require('./list.tpl');

class WaitingList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {data: []};
    }

    render () {
        return (
            <div className="user-waiting-list panel panel-default" onClick={this.props.onClick}>
                <div className="list-table"
                    dangerouslySetInnerHTML={{__html: template.fetch({detail: this.props.detail, data: this.state.data})}}
                >
                </div>
                <div className="detail-btn btn btn-default">
                    <i data-node="detail" className="to-detail glyphicon glyphicon-menu-right"></i>
                </div>
            </div>
        );
    }

    setData(data) {
        this.setState({data: data});
    }
}

export default WaitingList;