/**
 * @file 商户配送度量Store
 * @author chenkang01
 */

import flux from 'dispatcher/control';
import {createStore, bind} from 'alt/utils/decorators';
import AreasAction from 'actions/shopareas/AreasAction';

@createStore(flux)
class AreasStore {

    @bind(AreasAction.fetchInfo)
    infoReady(data) {
        this.type = 'info';
        this.infoData = data;
    }

    @bind(AreasAction.fetchList)
    listReady(opt) {
        this.type = 'list';
        this.listData = opt.data;
        this.mode = opt.mode;
        this.aoiid = opt.aoiid;
    }

    @bind(AreasAction.fetchAreas)
    AreasReady(data) {
        this.type = 'map';
        this.areasData = data;
    }
}

export default AreasStore;