/**
 * @file 商户 成本度量Store
 * @author chenkang01
 */

import flux from 'dispatcher/control';
import {createStore, bind} from 'alt/utils/decorators';
import CostAction from 'actions/shopcost/CostAction';

@createStore(flux)
class CostStore {

    @bind(CostAction.fetchInfo)
    infoReady(data) {
        this.type = 'info';
        this.infoData = data;
    }

    @bind(CostAction.fetchList)
    listReady(data) {
        this.type = 'list';
        this.listData = data;
    }

    @bind(CostAction.fetchCast)
    castReady(data) {
        this.type = 'map';
        this.castData = data;
    }
}

export default CostStore;