
import flux from 'dispatcher/control';
import {createStore, bind} from 'alt/utils/decorators';
import WaitingAction from 'actions/userwaiting/WaitingAction';

@createStore(flux)
class WaitingStore {
    @bind(WaitingAction.fetchInfo)
    infoReady(data) {
        this.type = 'info';
        this.infoData = data;
    }

    @bind(WaitingAction.fetchList)
    listReady(data) {
        this.type = 'list';
        this.listData = data;
    }

    @bind(WaitingAction.fetchCast)
    castReady(data) {
        this.type = 'map';
        this.castData = data;
    }
}

export default WaitingStore;